        var hrs = Math.floor(n / (60 * 60)) % 24;
        var days = Math.floor(n / (60 * 60 * 24)) % 365;
        var yrs = Math.floor(n / (60 * 60 * 24 * 365));
        var str = 'Uptime: ';
        if (yrs == 1) {
            str += yrs + ' year, ';
        } else if (yrs > 1) {
            str += yrs + ' years, ';
        }
        if (days == 1) {
            str += days + ' day, ';
        } else if (days > 1) {
            str += days + ' days, ';
        }
        if (hrs < 10) {
            str += '0';
        }
        str += hrs + ':';
        if (mins < 10) {
            str += '0';
        }
        str += mins;
        $('#uptime').text(str);
